/**
 * @file trajectory.h
 * @brief Trajectory data for Mecanum cart
 * 
 * This file contains trajectory arrays generated by MATLAB.
 * The trajectory consists of:
 * - vt[]: Velocity values (encoder counts per sample period, scaled)
 * - tt_clk[]: Time deltas between points (clock counts at 170MHz)
 * 
 * Special values:
 * - vt = 9999: End of trajectory marker
 * - vt = 0: Stop/pause point
 * 
 * To regenerate this file, use the MATLAB script:
 *   figure8_trajectory.m or rect_trajectory.m
 * which calls trajectory_to_c.m to export the data.
 */

#ifndef INC_TRAJECTORY_H_
#define INC_TRAJECTORY_H_

#include <stdint.h>

/* Placeholder trajectory - Figure-8 path
 * This is a simple placeholder that will be replaced by MATLAB-generated data.
 * The trajectory moves forward, then loops back in a figure-8 pattern.
 */

const uint32_t vt_len = 10;
const int16_t vt[10] = {
    0,      // Start at rest
    10,     // Accelerate
    20,     // Cruise
    20,     // Cruise
    10,     // Decelerate
    0,      // Stop
    0,      // Pause
    0,      // Pause
    9999,   // End marker
    9999    // End marker (padding)
};

const uint32_t tt_clk_len = 9;
const uint32_t tt_clk[9] = {
    17000000,   // 100ms at 170MHz
    17000000,   // 100ms
    17000000,   // 100ms
    17000000,   // 100ms
    17000000,   // 100ms
    17000000,   // 100ms
    17000000,   // 100ms
    17000000,   // 100ms
    17000000    // 100ms
};

#endif /* INC_TRAJECTORY_H_ */

